package day18_whilel_dowhileloop;

import java.util.*;

public class Print0to100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 0;

		while (num <= 100) {

			System.out.println(num);
			++num;// if we put before sout it starts from 1 not 0!!

		}

	}
}